import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProductCatalog from './ProductCatalog';
import ShoppingBag from './ShoppingBag';
import OrderConfirmation from './OrderConfirmation';
import CheckoutPage from './CheckoutPage'; // Import the new component

const HomePage = ({ token, handleLogout }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [cart, setCart] = useState(null);
    const [currentView, setCurrentView] = useState('catalog'); // 'catalog', 'bag', 'checkout', or 'orderConfirmation'
    const [placedOrder, setPlacedOrder] = useState(null);

    useEffect(() => {
        const fetchCart = async () => {
            if (currentView === 'catalog' || currentView === 'bag') {
                try {
                    const response = await fetch('http://localhost:8082/api/cart', {
                        headers: { 'Authorization': `Bearer ${token}` }
                    });
                    if (!response.ok) throw new Error('Failed to fetch cart');
                    const data = await response.json();
                    setCart(data);
                } catch (error) {
                    console.error("Error fetching cart:", error);
                }
            }
        };
        fetchCart();
    }, [token, currentView]);

    const handleSelectCategory = (category) => {
        setSelectedCategory(category);
        setCurrentView('catalog');
    };
    
    const handleCartUpdate = (updatedCart) => {
        setCart(updatedCart);
    };

    const handleNavigate = (view) => {
        setCurrentView(view);
    };

    const handleOrderPlaced = (order) => {
        setPlacedOrder(order);
        setCart(null); 
        setCurrentView('orderConfirmation');
    };

    const renderContent = () => {
        switch(currentView) {
            case 'bag':
                return <ShoppingBag token={token} cart={cart} onCartUpdate={handleCartUpdate} onNavigate={handleNavigate} />;
            case 'checkout':
                return <CheckoutPage token={token} onOrderPlaced={handleOrderPlaced} />;
            case 'orderConfirmation':
                return <OrderConfirmation order={placedOrder} onNavigate={handleNavigate} />;
            case 'catalog':
            default:
                return <ProductCatalog token={token} category={selectedCategory} onAddToCartSuccess={handleCartUpdate} />;
        }
    };

    const cartItemCount = cart ? cart.items.reduce((sum, item) => sum + item.quantity, 0) : 0;

    return (
        <div className="bg-gray-100 min-h-screen">
            <Header 
                handleLogout={handleLogout} 
                onSelectCategory={handleSelectCategory}
                cartItemCount={cartItemCount}
                onNavigate={handleNavigate}
            />
            <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {renderContent()}
            </div>
        </div>
    );
};

export default HomePage;
