import React, { useState } from 'react';
import FormInput from './common/FormInput';
import AlertMessage from './common/AlertMessage';

const PaymentPage = ({ token, cart, shippingDetails, onOrderPlaced }) => {
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [isProcessing, setIsProcessing] = useState(false);
    const [error, setError] = useState(null);

    if (!cart || !shippingDetails) {
        return <div className="text-center text-gray-500">Loading payment details...</div>;
    }

    const handlePayment = async (e) => {
        e.preventDefault();
        setIsProcessing(true);
        setError(null);

        try {
            // Step 1: Create the order to get an orderId
            const orderResponse = await fetch('http://localhost:8083/api/orders', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(shippingDetails)
            });
            const orderData = await orderResponse.json();
            if (!orderResponse.ok) {
                throw new Error(orderData.message || 'Failed to create order.');
            }

            // Step 2: Use the new orderId and amount to process the payment
            const paymentResponse = await fetch('http://localhost:8084/api/payments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    orderId: orderData.id,
                    amount: orderData.totalPrice 
                })
            });
            const paymentData = await paymentResponse.json();
            if (!paymentResponse.ok || paymentData.status !== 'COMPLETED') {
                 throw new Error(paymentData.message || 'Payment failed.');
            }

            // If both succeed, navigate to the confirmation page
            onOrderPlaced(orderData);

        } catch (err) {
            setError(err.message);
        } finally {
            setIsProcessing(false);
        }
    };
    
    const totalPrice = cart.items.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    return (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2 bg-white p-8 rounded-lg shadow-md border border-gray-200">
                <h1 className="text-2xl font-bold text-gray-800 mb-6">Enter Payment Details</h1>
                <form onSubmit={handlePayment} className="space-y-6">
                    {error && <AlertMessage message={error} type="error" />}
                    <FormInput id="cardNumber" type="text" label="Card Number" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} placeholder="•••• •••• •••• ••••" />
                    <div className="flex space-x-4">
                        <div className="w-1/2">
                            <FormInput id="expiryDate" type="text" label="Expiry Date" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} placeholder="MM/YY" />
                        </div>
                        <div className="w-1/2">
                            <FormInput id="cvv" type="text" label="CVV" value={cvv} onChange={(e) => setCvv(e.target.value)} placeholder="•••" />
                        </div>
                    </div>
                    <div>
                        <button 
                            type="submit"
                            disabled={isProcessing}
                            className="w-full mt-4 bg-slate-700 text-white py-3 rounded-md font-bold hover:bg-slate-800 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            {isProcessing ? 'Processing...' : `Pay $${totalPrice.toFixed(2)}`}
                        </button>
                    </div>
                </form>
            </div>
            <div className="lg:col-span-1">
                 <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                    <h2 className="text-lg font-semibold text-gray-700 mb-4 uppercase">Order Summary</h2>
                    <div className="space-y-2">
                        {cart.items.map(item => (
                            <div key={item.id} className="flex justify-between text-sm">
                                <span className="text-gray-600">{item.productName} x {item.quantity}</span>
                                <span className="font-medium">${(item.price * item.quantity).toFixed(2)}</span>
                            </div>
                        ))}
                    </div>
                    <hr className="my-4" />
                    <div className="flex justify-between font-bold text-lg text-gray-800">
                        <span>Total Amount</span>
                        <span>${totalPrice.toFixed(2)}</span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PaymentPage;
