import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProductCatalog from './ProductCatalog';

const HomePage = ({ token, handleLogout }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [cart, setCart] = useState(null);

    // Fetch the initial cart state when the page loads
    useEffect(() => {
        const fetchCart = async () => {
            try {
                const response = await fetch('http://localhost:8082/api/cart', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to fetch cart');
                const data = await response.json();
                setCart(data);
            } catch (error) {
                console.error("Error fetching cart:", error);
            }
        };
        fetchCart();
    }, [token]);

    const handleSelectCategory = (category) => {
        setSelectedCategory(category);
    };
    
    // This function will be called by ProductCatalog after a successful "Add to Cart"
    const handleAddToCartSuccess = (updatedCart) => {
        setCart(updatedCart);
    };

    // Calculate the total number of items in the cart
    const cartItemCount = cart ? cart.items.reduce((sum, item) => sum + item.quantity, 0) : 0;

    return (
        <div className="bg-gray-100 min-h-screen">
            <Header 
                handleLogout={handleLogout} 
                onSelectCategory={handleSelectCategory}
                cartItemCount={cartItemCount} 
            />
            <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                <ProductCatalog 
                    token={token} 
                    category={selectedCategory} 
                    onAddToCartSuccess={handleAddToCartSuccess}
                />
            </div>
        </div>
    );
};

export default HomePage;
