import React, { useState } from 'react';

const menuData = {
  men: [
    { title: 'Topwear', items: ['T-Shirts', 'Casual Shirts', 'Formal Shirts'] },
    { title: 'Bottomwear', items: ['Jeans', 'Pants', 'Shorts'] },
  ],
  women: [
    { title: 'Western Wear', items: ['Dresses', 'Tops', 'T-Shirts'] },
    { title: 'Indian & Fusion Wear', items: ['Kurtas & Suits', 'Sarees'] },
  ],
};

// Helper to generate the category string (e.g., "Men-T-Shirts")
const generateCategoryString = (mainCategory, item) => {
    const formattedItem = item.replace(/\s+/g, '-'); // Replace spaces with hyphens
    return `${mainCategory}-${formattedItem}`;
}

const NavLink = ({ children, onMouseEnter }) => (
  <div className="h-full flex items-center" onMouseEnter={onMouseEnter}>
    <a href="#" className="h-full flex items-center px-3 text-sm font-bold tracking-wider text-gray-700 uppercase border-b-4 border-transparent hover:border-pink-500 transition-all duration-200">
      {children}
    </a>
  </div>
);

const MegaMenu = ({ data, mainCategory, isVisible, onSelectCategory }) => {
    if (!isVisible) return null;
    return (
        <div className="absolute top-full left-0 min-w-max bg-white shadow-lg z-20 border-t border-gray-200 rounded-b-md">
            <div className="max-w-7xl mx-auto px-8 py-6">
                <div className="flex flex-row space-x-16">
                    {data.map((section) => (
                        <div key={section.title} className="flex-shrink-0">
                            <h3 className="text-sm font-semibold text-pink-500 mb-3 uppercase tracking-wider">{section.title}</h3>
                            <ul className="space-y-2">
                                {section.items.map((item) => (
                                    <li key={item}>
                                        <a href="#" 
                                           onClick={(e) => {
                                             e.preventDefault();
                                             onSelectCategory(generateCategoryString(mainCategory, item));
                                           }}
                                           className="block text-sm text-gray-600 hover:text-black">{item}</a>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};


const Header = ({ handleLogout, onSelectCategory }) => {
    const [activeMenu, setActiveMenu] = useState(null);

    const handleCategorySelect = (category) => {
        onSelectCategory(category);
        setActiveMenu(null); // Close menu on selection
    }

    return (
        <header className="bg-white border-b border-gray-200 text-gray-800 shadow-sm sticky top-0 z-30">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-20">
                    <div className="flex items-center h-full">
                        <div className="flex-shrink-0 mr-10">
                            {/* Make the logo clickable to show all products */}
                            <a href="#" onClick={(e) => { e.preventDefault(); onSelectCategory(null); }} className="cursor-pointer">
                                <svg width="53" height="35" viewBox="0 0 53 35" className="block h-10 w-auto">
                                    <path d="M41.43 28.35c0-3.03-1.64-5.04-4.5-5.04-2.85 0-4.5 2.01-4.5 5.04 0 3.03 1.65 5.04 4.5 5.04 2.85 0 4.5-2.01 4.5-5.04zm-6.22 0c0-1.84.86-2.73 1.72-2.73s1.72.89 1.72 2.73c0 1.84-.86 2.73-1.72 2.73s-1.72-.89-1.72-2.73zM52.37 32.94h-1.35l-2.4-3.52v3.52h-1.42V23.3h1.42l2.37 3.52v-3.52h1.42v9.64zM24.13 23.3l-3.3 9.64h-1.5l-3.3-9.64h1.53l2.48 7.52 2.5-7.52h1.58zM28.4 23.3h1.42v9.64h-1.42V23.3zM13.1 23.3h1.42v9.64h-1.42V23.3zM.5 23.3h1.42v9.64H.5V23.3z" fill="#282c3f"></path><path d="M12.11 23.3h-3.4v9.64h3.4v-1.35h-2v-2.8h2v-1.35h-2v-2.8h2V23.3z" fill="#282c3f"></path><path d="M51.8 1.5c-2.06-.25-3.8.3-5.32 1.6-1.53 1.3-2.48 3.1-2.48 5.42v1.9c0 2.33-.95 4.13 2.48 5.43 1.52 1.3 3.26 1.84 5.32 1.6v-2.3c-1.42.17-2.6-.17-3.52-.9-1-.73-1.48-1.84-1.48-3.22v-1.9c0-1.38.48-2.5 1.48-3.22.92-.73 2.1-.07 3.52-.9v-2.3z" fill="#FF3F6C"></path><path d="M3.7 1.5c2.05-.25 3.8.3 5.32 1.6 1.53 1.3 2.48 3.1 2.48 5.42v1.9c0 2.33-.95 4.13-2.48 5.43-1.52 1.3-3.27 1.84-5.32 1.6v-2.3c1.42.17 2.6-.17 3.52-.9-1-.73-1.48-1.84-1.48-3.22v-1.9c0-1.38.48-2.5-1.48-3.22-.92-.73-2.1-.07-3.52-.9v-2.3z" fill="#FB56C1"></path>
                                </svg>
                            </a>
                        </div>
                        <nav className="hidden md:flex h-full space-x-4 relative" onMouseLeave={() => setActiveMenu(null)}>
                            <NavLink onMouseEnter={() => setActiveMenu('men')}>MEN</NavLink>
                            <NavLink onMouseEnter={() => setActiveMenu('women')}>WOMEN</NavLink>
                            <MegaMenu data={menuData.men} mainCategory="Men" isVisible={activeMenu === 'men'} onSelectCategory={handleCategorySelect} />
                            <MegaMenu data={menuData.women} mainCategory="Women" isVisible={activeMenu === 'women'} onSelectCategory={handleCategorySelect} />
                        </nav>
                    </div>
                    <div className="flex items-center">
                        {/* ... search bar and other actions ... */}
                        <div className="hidden md:block relative mr-4">
                             <span className="absolute inset-y-0 left-0 flex items-center pl-3">
                                <svg className="h-5 w-5 text-gray-400" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd"></path></svg>
                            </span>
                            <input
                                className="w-full pl-10 pr-4 py-2 bg-gray-100 border border-gray-300 rounded-md text-sm placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-1 focus:ring-pink-500"
                                placeholder="Search for products, brands and more"
                            />
                        </div>
                        <div className="flex items-center space-x-4">
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 rounded-md text-sm font-medium text-white bg-pink-600 hover:bg-pink-700"
                            >
                                Log Out
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
