import React, { useState, useEffect } from 'react';

// --- Helper Components ---

const FormInput = ({ id, type, label, value, onChange, placeholder }) => (
  <div>
    <label htmlFor={id} className="block text-sm font-medium text-gray-300">
      {label}
    </label>
    <div className="mt-1">
      <input
        id={id}
        name={id}
        type={type}
        required
        className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm text-white"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
      />
    </div>
  </div>
);

const AlertMessage = ({ message, type }) => {
  if (!message) return null;
  const typeClasses = {
    error: "bg-red-900/30 text-red-300 border border-red-700",
    success: "bg-green-900/30 text-green-300 border border-green-700",
  };
  return (
    <div className={`px-4 py-3 rounded-md text-sm font-medium ${typeClasses[type]}`} role="alert">
      {message}
    </div>
  );
};

// --- Product Page Component ---
const ProductCatalog = ({ token, handleLogout }) => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                // Call your product-service running on port 8081
                const response = await fetch('http://localhost:8081/api/products', {
                    headers: {
                        // While this endpoint is public, it's good practice to send the token
                        // for endpoints that might be secured in the future.
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (err) {
                setError(err.message);
                console.error("Failed to fetch products:", err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchProducts();
    }, [token]);

    if (isLoading) {
        return <div className="text-center text-gray-400">Loading products...</div>;
    }

    if (error) {
        return <AlertMessage message={`Failed to load products: ${error}`} type="error" />;
    }

    return (
        <div className="bg-gray-900">
            <nav className="bg-gray-800 shadow-md">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex items-center justify-between h-16">
                        <div className="flex-shrink-0 text-white text-2xl font-bold">
                            Myntra
                        </div>
                        <div>
                            <button
                                onClick={handleLogout}
                                className="px-3 py-2 rounded-md text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700"
                            >
                                Log Out
                            </button>
                        </div>
                    </div>
                </div>
            </nav>
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                <div className="px-4 py-6 sm:px-0">
                    <h1 className="text-3xl font-bold text-white mb-6">Products</h1>
                    <div className="grid grid-cols-1 gap-y-10 sm:grid-cols-2 gap-x-6 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
                        {products.map((product) => (
                            <div key={product.id} className="group bg-gray-800 rounded-lg overflow-hidden shadow-lg">
                                <div className="w-full aspect-w-1 aspect-h-1 bg-gray-700 overflow-hidden">
                                    <img
                                        src={product.imageUrl}
                                        alt={product.name}
                                        className="w-full h-full object-center object-cover group-hover:opacity-75"
                                        onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/600x400/000000/FFFFFF?text=Image+Not+Found'; }}
                                    />
                                </div>
                                <div className="p-4">
                                    <h3 className="mt-1 text-lg font-semibold text-white">{product.name}</h3>
                                    <p className="mt-1 text-sm text-gray-400">{product.description}</p>
                                    <p className="mt-2 text-xl font-bold text-indigo-400">${product.price.toFixed(2)}</p>
                                    <button className="mt-4 w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 focus:ring-offset-gray-800">
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </main>
        </div>
    );
};


// --- Main App Component ---

export default function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [token, setToken] = useState(() => localStorage.getItem('jwt_token'));

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.error || `HTTP error! status: ${response.status}`);
      }
      const receivedToken = data.token;
      setToken(receivedToken);
      localStorage.setItem('jwt_token', receivedToken);
      setUsername('');
      setPassword('');
    } catch (err) {
      setError(err.message);
      console.error("Login failed:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    setToken(null);
    setError(null);
    localStorage.removeItem('jwt_token');
  };

  // If we have a token, show the Product Catalog.
  if (token) {
    return <ProductCatalog token={token} handleLogout={handleLogout} />;
  }

  // Otherwise, show the login form.
  return (
    <div className="min-h-screen bg-gray-900 flex flex-col justify-center py-12 sm:px-6 lg:px-8 font-sans">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-indigo-400">
          Sign in to your account
        </h2>
      </div>
      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-gray-800 py-8 px-4 shadow-lg rounded-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleLogin}>
            <AlertMessage message={error} type="error" />
            <FormInput
              id="username"
              type="text"
              label="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="testuser"
            />
            <FormInput
              id="password"
              type="password"
              label="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="••••••••"
            />
            <div>
              <button
                type="submit"
                disabled={isLoading}
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed focus:ring-offset-gray-800"
              >
                {isLoading ? 'Signing in...' : 'Sign in'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
