import React, { useState } from 'react';
import Header from './Header';
import ProductCatalog from './ProductCatalog';

const HomePage = ({ token, handleLogout }) => {
    // This component now manages which category is selected.
    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleSelectCategory = (category) => {
        setSelectedCategory(category);
    };

    return (
        <div className="bg-gray-100 min-h-screen">
            {/* Pass the handler function to the Header */}
            <Header handleLogout={handleLogout} onSelectCategory={handleSelectCategory} />
            <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {/* Pass the selected category to the ProductCatalog */}
                <ProductCatalog token={token} category={selectedCategory} />
            </div>
        </div>
    );
};

export default HomePage;
