import React, { useState, useEffect } from 'react';
import AlertMessage from './common/AlertMessage';

const ProductCatalog = ({ token, category, onAddToCartSuccess }) => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cartSuccessMessage, setCartSuccessMessage] = useState('');

    const handleAddToCart = async (productId) => {
        setCartSuccessMessage('');
        setError(null);
        try {
            const response = await fetch('http://localhost:8082/api/cart/items', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ productId: productId, quantity: 1 })
            });
            const data = await response.json(); // Get the updated cart from the response
            if (!response.ok) {
                throw new Error(data.message || 'Failed to add item to cart.');
            }
            setCartSuccessMessage('Item added to cart successfully!');
            onAddToCartSuccess(data); // Notify the parent component of the change
            setTimeout(() => setCartSuccessMessage(''), 3000);
        } catch (err) {
            setError(err.message);
        }
    };

    // This useEffect will now re-run whenever the 'category' prop changes.
    useEffect(() => {
        const fetchProducts = async () => {
            setIsLoading(true);
            setError(null);
            
            // Build the URL dynamically based on the selected category
            let url = 'http://localhost:8081/api/products';
            if (category) {
                url += `?category=${category}`;
            }

            try {
                const response = await fetch(url);
                if (!response.ok) throw new Error(`Failed to fetch products. Status: ${response.status}`);
                const data = await response.json();
                setProducts(data);
            } catch (err) {
                setError(err.message);
                setProducts([]); // Clear products on error
            } finally {
                setIsLoading(false);
            }
        };
        fetchProducts();
    }, [category]); // Dependency array includes 'category'

    return (
        <main>
            <div className="px-4 py-6 sm:px-0">
                <h1 className="text-3xl font-bold text-gray-800 mb-6">
                    {category ? category.replace(/-/g, ' ') : 'All Products'}
                </h1>
                {isLoading && <div className="text-center text-gray-500">Loading products...</div>}
                {error && <AlertMessage message={`Error: ${error}`} type="error" />}
                {cartSuccessMessage && <AlertMessage message={cartSuccessMessage} type="success" />}
                
                <div className="mt-6 grid grid-cols-1 gap-y-10 sm:grid-cols-2 gap-x-6 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
                    {products.length > 0 ? (
                        products.map((product) => (
                            <div key={product.id} className="group bg-white border border-gray-200 rounded-lg overflow-hidden shadow-sm flex flex-col">
                                <div className="w-full aspect-w-1 aspect-h-1 bg-gray-200 overflow-hidden">
                                    <img
                                        src={product.imageUrl}
                                        alt={product.name}
                                        className="w-full h-64 object-cover group-hover:opacity-75 transition-opacity"
                                        onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/600x400/F3F4F6/9CA3AF?text=Image+Not+Found'; }}
                                    />
                                </div>
                                <div className="p-4 flex flex-col flex-grow">
                                    <h3 className="mt-1 text-lg font-semibold text-gray-900">{product.name}</h3>
                                    <p className="mt-1 text-sm text-gray-600 flex-grow">{product.description}</p>
                                    <p className="mt-2 text-xl font-bold text-gray-800">${product.price.toFixed(2)}</p>
                                    <button 
                                        onClick={() => handleAddToCart(product.id)}
                                        className="mt-4 w-full bg-pink-600 text-white py-2 px-4 rounded-md hover:bg-pink-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500">
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        !isLoading && <p className="text-gray-500 col-span-full">No products found in this category.</p>
                    )}
                </div>
            </div>
        </main>
    );
};

export default ProductCatalog;
