import React, { useState } from 'react';

const AdminPage = ({ token, products, onProductUpdate }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [currentProduct, setCurrentProduct] = useState({
        id: null, name: '', description: '', price: '', category: '', stockQuantity: '', imageUrl: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentProduct({ ...currentProduct, [name]: value });
    };

    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;
        const formData = new FormData();
        formData.append('image', file);
        try {
            const response = await fetch('http://localhost:8081/api/products/images/upload', {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` },
                body: formData,
            });
            if (!response.ok) throw new Error('Image upload failed');
            const data = await response.json();
            setCurrentProduct({ ...currentProduct, imageUrl: data.imageUrl });
        } catch (error) {
            alert('Image upload failed. Please try again.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        if (!currentProduct.imageUrl) {
            alert('Please upload an image first.');
            setIsSubmitting(false);
            return;
        }

        const productData = {
            name: currentProduct.name,
            description: currentProduct.description,
            price: parseFloat(currentProduct.price),
            category: currentProduct.category,
            stockQuantity: parseInt(currentProduct.stockQuantity, 10),
            imageUrl: currentProduct.imageUrl
        };

        const url = isEditing ? `http://localhost:8081/api/products/${currentProduct.id}` : 'http://localhost:8081/api/products';
        const method = isEditing ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                // This header is crucial for both adding AND editing products
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productData)
            });

            if (!response.ok) {
                const errorData = await response.json().catch(() => ({ message: 'An unknown error occurred.' }));
                throw new Error(errorData.message);
            }

            resetForm();
            onProductUpdate();
        } catch (error) {
            alert(`An error occurred: ${error.message}`);
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleEdit = (product) => {
        setIsEditing(true);
        setCurrentProduct(product);
    };

    const handleDelete = async (productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            await fetch(`http://localhost:8081/api/products/${productId}`, { 
                method: 'DELETE',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            onProductUpdate();
        }
    };

    const resetForm = () => {
        setIsEditing(false);
        setCurrentProduct({
            id: null, name: '', description: '', price: '', category: '', stockQuantity: '', imageUrl: ''
        });
    };

    return (
        <div className="p-8">
            <h1 className="text-3xl font-bold mb-6">Admin - Product Management</h1>
            
            <div className="bg-white p-6 rounded-lg shadow-md mb-8">
                <h2 className="text-2xl font-semibold mb-4">{isEditing ? 'Edit Product' : 'Add New Product'}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input name="name" value={currentProduct.name} onChange={handleInputChange} placeholder="Product Name" className="w-full p-2 border rounded" required />
                    <textarea name="description" value={currentProduct.description} onChange={handleInputChange} placeholder="Product Description" className="w-full p-2 border rounded" required />
                    <input name="price" value={currentProduct.price} onChange={handleInputChange} placeholder="Price" type="number" step="0.01" className="w-full p-2 border rounded" required />
                    <input name="category" value={currentProduct.category} onChange={handleInputChange} placeholder="Category" className="w-full p-2 border rounded" required />
                    <input name="stockQuantity" value={currentProduct.stockQuantity} onChange={handleInputChange} placeholder="Stock Quantity" type="number" className="w-full p-2 border rounded" required />
                    <div>
                        <label className="block mb-2 text-sm font-medium">Product Image</label>
                        <input type="file" onChange={handleImageUpload} className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"/>
                        {currentProduct.imageUrl && <p className="text-sm mt-2 text-gray-600">Image ready to be saved: {currentProduct.imageUrl}</p>}
                    </div>
                    <div className="flex space-x-4 pt-2">
                        <button type="submit" disabled={isSubmitting} className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 shadow-sm disabled:opacity-50">{isSubmitting ? 'Saving...' : (isEditing ? 'Update Product' : 'Add Product')}</button>
                        {isEditing && <button type="button" onClick={resetForm} className="bg-gray-500 text-white px-6 py-2 rounded-md hover:bg-gray-600 shadow-sm">Cancel</button>}
                    </div>
                </form>
            </div>
            {/* Table of existing products */}
            <div className="bg-white p-6 rounded-lg shadow-md">
                 <h2 className="text-2xl font-semibold mb-4">Existing Products</h2>
                 <div className="overflow-x-auto">
                     <table className="min-w-full bg-white">
                         <thead className="bg-gray-200">
                             <tr>
                                 <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Name</th>
                                 <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Category</th>
                                 <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Price</th>
                                 <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Stock</th>
                                 <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Actions</th>
                             </tr>
                         </thead>
                         <tbody className="text-gray-700">
                             {products.map(product => (
                                 <tr key={product.id} className="border-b hover:bg-gray-50">
                                     <td className="py-3 px-4">{product.name}</td>
                                     <td className="py-3 px-4">{product.category}</td>
                                     <td className="py-3 px-4">${typeof product.price === 'number' ? product.price.toFixed(2) : '0.00'}</td>
                                     <td className="py-3 px-4">{product.stockQuantity}</td>
                                     <td className="py-3 px-4">
                                         <button onClick={() => handleEdit(product)} className="text-blue-500 hover:underline mr-4">Edit</button>
                                         <button onClick={() => handleDelete(product.id)} className="text-red-500 hover:underline">Delete</button>
                                     </td>
                                 </tr>
                             ))}
                         </tbody>
                     </table>
                 </div>
             </div>
        </div>
    );
};

export default AdminPage;
