import React, { useState } from 'react';
import FormInput from './common/FormInput';
import AlertMessage from './common/AlertMessage';

const CheckoutPage = ({ onAddressSubmit }) => {
    const [name, setName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [shippingAddress, setShippingAddress] = useState('');
    const [city, setCity] = useState('');
    const [zipCode, setZipCode] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!name || !phoneNumber || !shippingAddress || !city || !zipCode) {
            setError('Please fill out all fields.');
            return;
        }
        const addressDetails = { name, phoneNumber, shippingAddress, city, zipCode };
        onAddressSubmit(addressDetails);
    };

    return (
        <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-md border border-gray-200">
            <h1 className="text-2xl font-bold text-gray-800 mb-6">Confirm Your Shipping Details</h1>
            <form onSubmit={handleSubmit} className="space-y-6">
                {error && <AlertMessage message={error} type="error" />}
                <FormInput id="name" type="text" label="Full Name" value={name} onChange={(e) => setName(e.target.value)} placeholder="Your Name" />
                <FormInput id="phoneNumber" type="tel" label="Phone Number" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="123-456-7890" />
                <FormInput id="shippingAddress" type="text" label="Shipping Address" value={shippingAddress} onChange={(e) => setShippingAddress(e.target.value)} placeholder="123 Vision St" />
                <FormInput id="city" type="text" label="City" value={city} onChange={(e) => setCity(e.target.value)} placeholder="Styleburg" />
                <FormInput id="zipCode" type="text" label="Zip Code" value={zipCode} onChange={(e) => setZipCode(e.target.value)} placeholder="54321" />
                <div>
                    <button 
                        type="submit"
                        className="w-full mt-4 bg-slate-700 text-white py-3 rounded-md font-bold hover:bg-slate-800"
                    >
                        Proceed to Payment
                    </button>
                </div>
            </form>
        </div>
    );
};

export default CheckoutPage;
