import React from 'react';

const ProductCatalog = ({ token, products, onAddToCartSuccess }) => {

    const handleAddToCart = async (productId) => {
        try {
            const response = await fetch('http://localhost:8082/api/cart/items', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ productId: productId, quantity: 1 })
            });
            if (!response.ok) throw new Error('Failed to add item to cart');
            
            const updatedCart = await response.json();
            onAddToCartSuccess(updatedCart); // Notify HomePage that the cart has changed
        } catch (error) {
            console.error("Error adding to cart:", error);
        }
    };

    return (
        <div className="bg-white shadow-sm rounded-lg p-6">
            <h2 className="text-2xl font-bold tracking-tight text-gray-900 mb-6">All Products</h2>
            
            {products.length === 0 ? (
                <p className="text-gray-500">No products found.</p>
            ) : (
                <div className="grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
                    {products.map((product) => (
                        <div key={product.id} className="group relative border p-4 rounded-lg shadow-sm hover:shadow-lg transition-shadow">
                            <div className="w-full min-h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-80 lg:aspect-none">
                                <img
                                    src={`http://localhost:8081${product.imageUrl}`}
                                    alt={product.name}
                                    className="w-full h-full object-center object-cover lg:w-full lg:h-full"
                                />
                            </div>
                            <div className="mt-4 flex justify-between">
                                <div>
                                    <h3 className="text-sm text-gray-700 font-semibold">
                                        <span aria-hidden="true" className="absolute inset-0" />
                                        {product.name}
                                    </h3>
                                    <p className="mt-1 text-sm text-gray-500">{product.description}</p>
                                </div>
                                <p className="text-sm font-medium text-gray-900">${product.price.toFixed(2)}</p>
                            </div>
                            <button
                                onClick={() => handleAddToCart(product.id)}
                                className="mt-4 w-full bg-slate-700 text-white py-2 px-4 rounded-md font-bold hover:bg-slate-800"
                            >
                                Add to Cart
                            </button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ProductCatalog;
