import React, { useState } from 'react';

const menuData = {
  men: [
    { title: 'Topwear', items: ['T-Shirts', 'Casual Shirts', 'Formal Shirts', 'Sweatshirts', 'Sweaters'] },
    { title: 'Bottomwear', items: ['Jeans', 'Casual Trousers', 'Formal Trousers', 'Shorts'] },
    { title: 'Innerwear & Sleepwear', items: ['Briefs & Trunks', 'Boxers'] },
  ],
  women: [
    { title: 'Indian & Fusion Wear', items: ['Kurtas & Suits', 'Sarees', 'Ethnic Wear'] },
    { title: 'Western Wear', items: ['Dresses', 'Tops', 'T-shirts', 'Jeans', 'Trousers & Capris', 'Shorts'] },
    { title: 'Lingerie & Sleepwear', items: ['Bra', 'Briefs', 'Sleepwear'] },
  ],
};

const NavLink = ({ children, onMouseEnter, onMouseLeave }) => (
  <div className="h-full flex items-center" onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
    <a href="#" className="h-full flex items-center px-3 text-sm font-bold tracking-wider text-gray-700 uppercase border-b-4 border-transparent hover:border-slate-500 transition-all duration-200">
      {children}
    </a>
  </div>
);

const MegaMenu = ({ data, isVisible, onSelectCategory, mainCategory }) => {
    if (!isVisible) return null;
    const generateCategoryString = (mainCat, item) => `${mainCat}-${item.replace(/\s+/g, '-')}`;
    return (
        <div className="absolute top-full left-0 min-w-max bg-white shadow-lg z-20 border-t border-gray-200 rounded-b-md">
            <div className="max-w-7xl mx-auto px-8 py-6">
                <div className="flex flex-row space-x-16">
                    {data.map((section) => (
                        <div key={section.title} className="flex-shrink-0">
                            <h3 className="text-sm font-semibold text-slate-600 mb-3 uppercase tracking-wider">{section.title}</h3>
                            <ul className="space-y-2">
                                {section.items.map((item) => (
                                    <li key={item}>
                                        <a href="#" onClick={(e) => { e.preventDefault(); onSelectCategory(generateCategoryString(mainCategory, item)); }} className="block text-sm text-gray-600 hover:text-black">{item}</a>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

// Change 1: Add userRole to the list of props
const Header = ({ handleLogout, onSelectCategory, cartItemCount, onNavigate, userRole, onSearch}) => {
    const [localSearch, setLocalSearch] = useState('');

    const handleSearchChange = (e) => {
        setLocalSearch(e.target.value);
    };
    
    const handleSearchSubmit = (e) => {
        e.preventDefault();
        onSearch(localSearch);
    };
    const [activeMenu, setActiveMenu] = useState(null);

    const handleCategorySelect = (category) => {
        onSelectCategory(category);
        setActiveMenu(null);
    };

    return (
        <header className="bg-white border-b border-gray-200 text-gray-800 shadow-sm sticky top-0 z-30">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-20">
                    <div className="flex items-center h-full">
                        <div className="flex-shrink-0 mr-10">
                            <a href="#" onClick={(e) => { e.preventDefault(); onNavigate('catalog'); onSelectCategory(null); }} className="flex items-center space-x-2 cursor-pointer">
                                <span className="h-10 w-10 bg-slate-800 rounded-full flex items-center justify-center text-white font-bold text-xl">
                                    RV
                                </span>
                            </a>
                        </div>
                        <nav className="hidden md:flex h-full space-x-4 relative" onMouseLeave={() => setActiveMenu(null)}>
                            <NavLink onMouseEnter={() => setActiveMenu('men')}>MEN</NavLink>
                            <NavLink onMouseEnter={() => setActiveMenu('women')}>WOMEN</NavLink>
                            <MegaMenu data={menuData.men} mainCategory="Men" isVisible={activeMenu === 'men'} onSelectCategory={handleCategorySelect} />
                            <MegaMenu data={menuData.women} mainCategory="Women" isVisible={activeMenu === 'women'} onSelectCategory={handleCategorySelect} />
                        </nav>
                    </div>
                    <div className="flex items-center">
                        <div className="hidden md:block relative mr-4">
                             <span className="absolute inset-y-0 left-0 flex items-center pl-3">
                                <svg className="h-5 w-5 text-gray-400" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd"></path></svg>
                            </span>
                            <input
                                className="w-full pl-10 pr-4 py-2 bg-gray-100 border border-gray-300 rounded-md text-sm placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-1 focus:ring-slate-500"
                                placeholder="Search for products, brands and more"
                            />
                        </div>
                        <div className="flex items-center space-x-6 ml-6">
                             {/* Change 2: Only show the Admin button if the user has the 'ROLE_ADMIN' */}
                             {userRole === 'ROLE_ADMIN' && (
                                <button onClick={() => onNavigate('admin')} className="flex flex-col items-center text-xs font-bold text-gray-700 hover:text-slate-600">
                                    <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                                        <path strokeLinecap="round" strokeLinejoin="round" d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.096 2.572-1.065z" />
                                        <path strokeLinecap="round" strokeLinejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                    </svg>
                                    <span>Admin</span>
                                </button>
                             )}
                            <button onClick={() => onNavigate('bag')} className="relative flex flex-col items-center text-xs font-bold text-gray-700 hover:text-slate-600">
                                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                                </svg>
                                <span>Bag</span>
                                {cartItemCount > 0 && (
                                    <span className="absolute -top-2 -right-2 flex items-center justify-center h-5 w-5 bg-slate-600 text-white text-xs rounded-full">
                                        {cartItemCount}
                                    </span>
                                )}
                            </button>
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 rounded-md text-sm font-medium text-white bg-slate-700 hover:bg-slate-800"
                            >
                                Log Out
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
