import React, { useState, useEffect } from 'react';

const AdminPage = ({ token }) => {
    const [products, setProducts] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [currentProduct, setCurrentProduct] = useState({
        id: null,
        name: '',
        description: '',
        price: '',
        category: '',
        stockQuantity: '',
        imageUrl: ''
    });

    // Fetch all products when the component mounts
    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        const response = await fetch('http://localhost:8081/api/products');
        const data = await response.json();
        setProducts(data);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentProduct({ ...currentProduct, [name]: value });
    };

    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('image', file);

        try {
            const response = await fetch('http://localhost:8081/api/products/images/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            setCurrentProduct({ ...currentProduct, imageUrl: data.imageUrl });
        } catch (error) {
            console.error('Image upload failed:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = isEditing
            ? `http://localhost:8081/api/products/${currentProduct.id}`
            : 'http://localhost:8081/api/products';
        const method = isEditing ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(currentProduct)
        });

        resetForm();
        fetchProducts();
    };

    const handleEdit = (product) => {
        setIsEditing(true);
        setCurrentProduct(product);
    };

    const handleDelete = async (productId) => {
        await fetch(`http://localhost:8081/api/products/${productId}`, {
            method: 'DELETE'
        });
        fetchProducts();
    };

    const resetForm = () => {
        setIsEditing(false);
        setCurrentProduct({
            id: null, name: '', description: '', price: '', category: '', stockQuantity: '', imageUrl: ''
        });
    };

    return (
        <div className="p-8">
            <h1 className="text-3xl font-bold mb-6">Admin - Product Management</h1>
            
            {/* Product Form */}
            <div className="bg-white p-6 rounded-lg shadow-md mb-8">
                <h2 className="text-2xl font-semibold mb-4">{isEditing ? 'Edit Product' : 'Add New Product'}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input name="name" value={currentProduct.name} onChange={handleInputChange} placeholder="Product Name" className="w-full p-2 border rounded" required />
                    <textarea name="description" value={currentProduct.description} onChange={handleInputChange} placeholder="Product Description" className="w-full p-2 border rounded" required />
                    <input name="price" value={currentProduct.price} onChange={handleInputChange} placeholder="Price" type="number" className="w-full p-2 border rounded" required />
                    <input name="category" value={currentProduct.category} onChange={handleInputChange} placeholder="Category" className="w-full p-2 border rounded" required />
                    <input name="stockQuantity" value={currentProduct.stockQuantity} onChange={handleInputChange} placeholder="Stock Quantity" type="number" className="w-full p-2 border rounded" required />
                    
                    <div>
                        <label className="block mb-2">Product Image</label>
                        <input type="file" onChange={handleImageUpload} className="w-full p-2 border rounded" />
                        {currentProduct.imageUrl && <p className="text-sm mt-2">Image URL: {currentProduct.imageUrl}</p>}
                    </div>

                    <div className="flex space-x-4">
                        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">{isEditing ? 'Update Product' : 'Add Product'}</button>
                        <button type="button" onClick={resetForm} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Cancel</button>
                    </div>
                </form>
            </div>

            {/* Products Table */}
            <div className="bg-white p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-semibold mb-4">Existing Products</h2>
                <div className="overflow-x-auto">
                    <table className="min-w-full bg-white">
                        <thead className="bg-gray-200">
                            <tr>
                                <th className="py-2 px-4">Name</th>
                                <th className="py-2 px-4">Category</th>
                                <th className="py-2 px-4">Price</th>
                                <th className="py-2 px-4">Stock</th>
                                <th className="py-2 px-4">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {products.map(product => (
                                <tr key={product.id} className="border-b">
                                    <td className="py-2 px-4">{product.name}</td>
                                    <td className="py-2 px-4">{product.category}</td>
                                    <td className="py-2 px-4">${product.price.toFixed(2)}</td>
                                    <td className="py-2 px-4">{product.stockQuantity}</td>
                                    <td className="py-2 px-4">
                                        <button onClick={() => handleEdit(product)} className="text-blue-500 hover:underline mr-4">Edit</button>
                                        <button onClick={() => handleDelete(product.id)} className="text-red-500 hover:underline">Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminPage;
