import React, { useState } from 'react';
import FormInput from './common/FormInput';
import AlertMessage from './common/AlertMessage';

const CheckoutPage = ({ token, onOrderPlaced }) => {
    const [shippingAddress, setShippingAddress] = useState('');
    const [city, setCity] = useState('');
    const [zipCode, setZipCode] = useState('');
    const [isPlacingOrder, setIsPlacingOrder] = useState(false);
    const [error, setError] = useState(null);

    const handleConfirmOrder = async (e) => {
        e.preventDefault();
        setIsPlacingOrder(true);
        setError(null);
        try {
            const response = await fetch('http://localhost:8083/api/orders', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ shippingAddress, city, zipCode })
            });
            const orderData = await response.json();
            if (!response.ok) {
                throw new Error(orderData.message || 'Failed to place order.');
            }
            onOrderPlaced(orderData);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsPlacingOrder(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-md border border-gray-200">
            <h1 className="text-2xl font-bold text-gray-800 mb-6">Confirm Your Details</h1>
            <form onSubmit={handleConfirmOrder} className="space-y-6">
                {error && <AlertMessage message={error} type="error" />}
                <FormInput id="shippingAddress" type="text" label="Shipping Address" value={shippingAddress} onChange={(e) => setShippingAddress(e.target.value)} placeholder="123 Vision St" />
                <FormInput id="city" type="text" label="City" value={city} onChange={(e) => setCity(e.target.value)} placeholder="Styleburg" />
                <FormInput id="zipCode" type="text" label="Zip Code" value={zipCode} onChange={(e) => setZipCode(e.target.value)} placeholder="54321" />
                <div>
                    <button 
                        type="submit"
                        disabled={isPlacingOrder}
                        className="w-full mt-4 bg-slate-700 text-white py-3 rounded-md font-bold hover:bg-slate-800 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {isPlacingOrder ? 'Confirming...' : 'Confirm & Pay'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default CheckoutPage;
