import React, { useState } from 'react';
import AlertMessage from './common/AlertMessage';

const ShoppingBag = ({ token, cart, onCartUpdate }) => {
    const [error, setError] = useState(null);

    if (!cart) {
        return <div className="text-center text-gray-500">Loading your bag...</div>;
    }

    const handleQuantityChange = async (itemId, newQuantity) => {
        setError(null);
        try {
            const response = await fetch(`http://localhost:8082/api/cart/items/${itemId}`, {
                method: 'PUT', // Use PUT for update
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ quantity: newQuantity })
            });
            const updatedCart = await response.json();
            if (!response.ok) {
                throw new Error(updatedCart.message || 'Failed to update quantity.');
            }
            onCartUpdate(updatedCart); // Notify parent of the change
        } catch (err) {
            setError(err.message);
        }
    };
    
    const handleRemoveItem = (itemId) => {
        // Removing is now just setting the quantity to 0
        handleQuantityChange(itemId, 0);
    };

    const totalPrice = cart.items.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    return (
        <div className="bg-gray-100 p-4 sm:p-6">
            <h1 className="text-2xl font-bold text-gray-800 mb-6">Shopping Bag</h1>
            {error && <AlertMessage message={error} type="error" />}
            {cart.items.length === 0 ? (
                <p className="text-gray-600">Your bag is empty.</p>
            ) : (
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                    {/* Cart Items Column */}
                    <div className="lg:col-span-2 space-y-4">
                        {cart.items.map(item => (
                            <div key={item.id} className="flex bg-white p-4 rounded-lg shadow-sm border border-gray-200 items-center">
                                <div className="w-24 h-24 bg-gray-200 rounded-md overflow-hidden mr-4 flex-shrink-0">
                                     <img src={`https://placehold.co/200x200/F3F4F6/9CA3AF?text=${item.productName.replace(/\s+/g,'+')}`} alt={item.productName} className="w-full h-full object-cover" />
                                </div>
                                <div className="flex-grow">
                                    <h3 className="font-semibold text-gray-800">{item.productName}</h3>
                                    <p className="font-bold text-gray-900 mt-2">${(item.price * item.quantity).toFixed(2)}</p>
                                    
                                    {/* --- NEW: Quantity Dropdown --- */}
                                    <div className="mt-2">
                                        <label htmlFor={`quantity-${item.id}`} className="text-sm font-medium text-gray-500 mr-2">Qty:</label>
                                        <select
                                            id={`quantity-${item.id}`}
                                            value={item.quantity}
                                            onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                                            className="p-1 border border-gray-300 rounded-md text-sm"
                                        >
                                            {[...Array(10).keys()].map(i => 
                                                <option key={i} value={i}>{i === 0 ? '0 (Remove)' : i}</option>
                                            )}
                                        </select>
                                    </div>
                                </div>
                                <div className="flex flex-col justify-start items-end ml-4">
                                    <button onClick={() => handleRemoveItem(item.id)} className="text-red-500 hover:text-red-700 font-semibold text-sm">
                                        REMOVE
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Price Details Column */}
                    <div className="lg:col-span-1">
                        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                            <h2 className="text-lg font-semibold text-gray-700 mb-4 uppercase">Price Details</h2>
                            <div className="space-y-2">
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Total MRP</span>
                                    <span>${totalPrice.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between text-green-600">
                                    <span className="text-gray-600">Discount on MRP</span>
                                    <span>-$0.00</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Convenience Fee</span>
                                    <span className="text-green-600">FREE</span>
                                </div>
                            </div>
                            <hr className="my-4" />
                            <div className="flex justify-between font-bold text-lg">
                                <span>Total Amount</span>
                                <span>${totalPrice.toFixed(2)}</span>
                            </div>
                            <button className="w-full mt-6 bg-slate-700 text-white py-3 rounded-md font-bold hover:bg-slate-800">
                                PLACE ORDER
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ShoppingBag;
