import React, { useState } from 'react';
import FormInput from './common/FormInput';
import AlertMessage from './common/AlertMessage';

const AuthForm = ({ onLoginSuccess }) => {
    const [isLoginView, setIsLoginView] = useState(true);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true); setError(null); setSuccess(null);
        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Login failed');
            onLoginSuccess(data.token);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setIsLoading(true); setError(null); setSuccess(null);
        try {
            const response = await fetch('http://localhost:8080/api/auth/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, email, password, role: 'USER' }),
            });
            const data = await response.text();
            if (!response.ok) throw new Error(data || 'Registration failed');
            setSuccess('Registration successful! Please log in.');
            setIsLoginView(true);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 flex flex-col justify-center py-12 sm:px-6 lg:px-8 font-sans">
            <div className="sm:mx-auto sm:w-full sm:max-w-md">
                <h2 className="mt-6 text-center text-3xl font-extrabold text-indigo-400">
                    {isLoginView ? 'Sign in to your account' : 'Create a new account'}
                </h2>
            </div>
            <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
                <div className="bg-gray-800 py-8 px-4 shadow-lg rounded-lg sm:px-10">
                    <form className="space-y-6" onSubmit={isLoginView ? handleLogin : handleRegister}>
                        <AlertMessage message={error} type="error" />
                        <AlertMessage message={success} type="success" />
                        <FormInput id="username" type="text" label="Username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="your-username" />
                        {!isLoginView && (
                            <FormInput id="email" type="email" label="Email address" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
                        )}
                        <FormInput id="password" type="password" label="Password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="••••••••" />
                        <div>
                            <button type="submit" disabled={isLoading} className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed focus:ring-offset-gray-800">
                                {isLoading ? 'Processing...' : (isLoginView ? 'Sign in' : 'Register')}
                            </button>
                        </div>
                    </form>
                    <div className="mt-6 text-center">
                        <button onClick={() => { setIsLoginView(!isLoginView); setError(null); setSuccess(null); }} className="text-sm text-indigo-400 hover:text-indigo-300">
                            {isLoginView ? 'Not an existing user? Register here' : 'Already have an account? Sign in'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};
export default AuthForm;
