import React, { useState } from 'react';

const menuData = {
  men: [
    { title: 'Topwear', items: ['T-Shirts', 'Casual Shirts', 'Formal Shirts', 'Sweatshirts', 'Sweaters'] },
    { title: 'Bottomwear', items: ['Jeans', 'Casual Trousers', 'Formal Trousers', 'Shorts'] },
    { title: 'Innerwear & Sleepwear', items: ['Briefs & Trunks', 'Boxers'] },
  ],
  women: [
    { title: 'Indian & Fusion Wear', items: ['Kurtas & Suits', 'Sarees', 'Ethnic Wear'] },
    { title: 'Western Wear', items: ['Dresses', 'Tops', 'T-shirts', 'Jeans', 'Trousers & Capris', 'Shorts'] },
    { title: 'Lingerie & Sleepwear', items: ['Bra', 'Briefs', 'Sleepwear'] },
  ],
};

const NavLink = ({ children, onMouseEnter, onMouseLeave }) => (
  <div className="h-full flex items-center" onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
    <a href="#" className="h-full flex items-center px-3 text-sm font-bold tracking-wider text-gray-200 uppercase border-b-4 border-transparent hover:border-pink-500 transition-all duration-200">
      {children}
    </a>
  </div>
);

// --- THIS COMPONENT IS FIXED ---
const MegaMenu = ({ data, isVisible }) => {
    if (!isVisible) return null;
    return (
        <div className="absolute top-full left-0 min-w-max bg-white shadow-lg z-20 border-t border-gray-200 rounded-b-md">
            <div className="max-w-7xl mx-auto px-8 py-6">
                {/* Replaced Grid with Flexbox for better alignment */}
                <div className="flex flex-row space-x-16">
                    {data.map((section) => (
                        <div key={section.title} className="flex-shrink-0">
                            <h3 className="text-sm font-semibold text-pink-500 mb-3 uppercase tracking-wider">{section.title}</h3>
                            <ul className="space-y-2">
                                {section.items.map((item) => (
                                    <li key={item}>
                                        <a href="#" className="block text-sm text-gray-600 hover:text-black">{item}</a>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};


const Header = ({ handleLogout }) => {
    const [activeMenu, setActiveMenu] = useState(null);

    return (
        <header className="bg-gray-900 border-b border-gray-700 text-white shadow-md sticky top-0 z-30">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-20">
                    <div className="flex items-center h-full">
                        <div className="flex-shrink-0 mr-10">
                             <svg width="53" height="35" viewBox="0 0 53 35" className="block h-10 w-auto">
                                <path d="M41.43 28.35c0-3.03-1.64-5.04-4.5-5.04-2.85 0-4.5 2.01-4.5 5.04 0 3.03 1.65 5.04 4.5 5.04 2.85 0 4.5-2.01 4.5-5.04zm-6.22 0c0-1.84.86-2.73 1.72-2.73s1.72.89 1.72 2.73c0 1.84-.86 2.73-1.72 2.73s-1.72-.89-1.72-2.73zM52.37 32.94h-1.35l-2.4-3.52v3.52h-1.42V23.3h1.42l2.37 3.52v-3.52h1.42v9.64zM24.13 23.3l-3.3 9.64h-1.5l-3.3-9.64h1.53l2.48 7.52 2.5-7.52h1.58zM28.4 23.3h1.42v9.64h-1.42V23.3zM13.1 23.3h1.42v9.64h-1.42V23.3zM.5 23.3h1.42v9.64H.5V23.3z" fill="#f2f2f2"></path><path d="M12.11 23.3h-3.4v9.64h3.4v-1.35h-2v-2.8h2v-1.35h-2v-2.8h2V23.3z" fill="#f2f2f2"></path><path d="M51.8 1.5c-2.06-.25-3.8.3-5.32 1.6-1.53 1.3-2.48 3.1-2.48 5.42v1.9c0 2.33.95 4.13 2.48 5.43 1.52 1.3 3.26 1.84 5.32 1.6v-2.3c-1.42.17-2.6-.17-3.52-.9-1-.73-1.48-1.84-1.48-3.22v-1.9c0-1.38.48-2.5 1.48-3.22.92-.73 2.1-.07 3.52-.9v-2.3z" fill="#FF3F6C"></path><path d="M3.7 1.5c2.05-.25 3.8.3 5.32 1.6 1.53 1.3 2.48 3.1 2.48 5.42v1.9c0 2.33-.95 4.13-2.48 5.43-1.52 1.3-3.27 1.84-5.32 1.6v-2.3c1.42.17 2.6-.17 3.52-.9-1-.73-1.48-1.84-1.48-3.22v-1.9c0-1.38-.48-2.5-1.48-3.22-.92-.73-2.1-.07-3.52-.9v-2.3z" fill="#FB56C1"></path>
                            </svg>
                        </div>
                        <nav className="hidden md:flex h-full space-x-4 relative" onMouseLeave={() => setActiveMenu(null)}>
                            <NavLink onMouseEnter={() => setActiveMenu('men')}>MEN</NavLink>
                            <NavLink onMouseEnter={() => setActiveMenu('women')}>WOMEN</NavLink>
                            <MegaMenu data={menuData.men} isVisible={activeMenu === 'men'} />
                            <MegaMenu data={menuData.women} isVisible={activeMenu === 'women'} />
                        </nav>
                    </div>
                    <div className="flex items-center">
                        <div className="hidden md:block relative mr-4">
                             <span className="absolute inset-y-0 left-0 flex items-center pl-3">
                                <svg className="h-5 w-5 text-gray-400" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd"></path></svg>
                            </span>
                            <input
                                className="w-full pl-10 pr-4 py-2 bg-gray-800 border border-gray-700 rounded-md text-sm placeholder-gray-400 text-white focus:outline-none focus:ring-1 focus:ring-pink-500"
                                placeholder="Search for products, brands and more"
                            />
                        </div>
                        <div className="flex items-center space-x-4">
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 rounded-md text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700"
                            >
                                Log Out
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
