import React, { useState, useEffect } from 'react';

// --- Reusable Helper Components ---

const FormInput = ({ id, type, label, value, onChange, placeholder }) => (
  <div>
    <label htmlFor={id} className="block text-sm font-medium text-gray-300">
      {label}
    </label>
    <div className="mt-1">
      <input
        id={id}
        name={id}
        type={type}
        required
        className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm text-white"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
      />
    </div>
  </div>
);

const AlertMessage = ({ message, type }) => {
  if (!message) return null;
  const typeClasses = {
    error: "bg-red-900/30 text-red-300 border border-red-700",
    success: "bg-green-900/30 text-green-300 border border-green-700",
  };
  return (
    <div className={`px-4 py-3 rounded-md text-sm font-medium ${typeClasses[type]}`} role="alert">
      {message}
    </div>
  );
};


// --- Authentication Forms Component ---
// This component handles both Login and Registration views.

const AuthForm = ({ onLoginSuccess }) => {
    const [isLoginView, setIsLoginView] = useState(true);

    // State for form fields
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');

    // State for UI feedback
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);
        setSuccess(null);
        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Login failed');
            // On success, call the function passed from the parent App component
            onLoginSuccess(data.token);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);
        setSuccess(null);
        try {
            const response = await fetch('http://localhost:8080/api/auth/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, email, password, role: 'USER' }),
            });
            const data = await response.text();
            if (!response.ok) throw new Error(data || 'Registration failed');
            setSuccess('Registration successful! Please log in.');
            setIsLoginView(true); // Switch to login view after successful registration
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 flex flex-col justify-center py-12 sm:px-6 lg:px-8 font-sans">
            <div className="sm:mx-auto sm:w-full sm:max-w-md">
                <h2 className="mt-6 text-center text-3xl font-extrabold text-indigo-400">
                    {isLoginView ? 'Sign in to your account' : 'Create a new account'}
                </h2>
            </div>

            <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
                <div className="bg-gray-800 py-8 px-4 shadow-lg rounded-lg sm:px-10">
                    <form className="space-y-6" onSubmit={isLoginView ? handleLogin : handleRegister}>
                        <AlertMessage message={error} type="error" />
                        <AlertMessage message={success} type="success" />

                        <FormInput id="username" type="text" label="Username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="your-username" />

                        {!isLoginView && (
                            <FormInput id="email" type="email" label="Email address" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
                        )}

                        <FormInput id="password" type="password" label="Password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="••••••••" />

                        <div>
                            <button type="submit" disabled={isLoading} className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed focus:ring-offset-gray-800">
                                {isLoading ? 'Processing...' : (isLoginView ? 'Sign in' : 'Register')}
                            </button>
                        </div>
                    </form>

                    <div className="mt-6 text-center">
                        <button onClick={() => { setIsLoginView(!isLoginView); setError(null); setSuccess(null); }} className="text-sm text-indigo-400 hover:text-indigo-300">
                            {isLoginView ? 'Not an existing user? Register here' : 'Already have an account? Sign in'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};


// --- Product Page Component ---
// This component displays the product catalog after a user logs in.

const ProductCatalog = ({ token, handleLogout }) => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            setIsLoading(true);
            setError(null);
            try {
                const response = await fetch('http://localhost:8081/api/products');
                if (!response.ok) {
                    throw new Error(`Failed to fetch products. Status: ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (err) {
                setError(err.message);
                console.error("Failed to fetch products:", err);
            } finally {
                setIsLoading(false);
            }
        };

        fetchProducts();
    }, []);

    return (
        <div className="bg-gray-900 min-h-screen">
            <nav className="bg-gray-800 shadow-md sticky top-0 z-10">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex items-center justify-between h-16">
                        <div className="flex-shrink-0 text-white text-2xl font-bold">
                            Myntra
                        </div>
                        <div>
                            <button
                                onClick={handleLogout}
                                className="px-3 py-2 rounded-md text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700"
                            >
                                Log Out
                            </button>
                        </div>
                    </div>
                </div>
            </nav>
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                <div className="px-4 py-6 sm:px-0">
                    <h1 className="text-3xl font-bold text-white mb-6">Products</h1>
                    {isLoading && <div className="text-center text-gray-400">Loading products...</div>}
                    {error && <AlertMessage message={`Error: ${error}`} type="error" />}
                    <div className="grid grid-cols-1 gap-y-10 sm:grid-cols-2 gap-x-6 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
                        {products.map((product) => (
                            <div key={product.id} className="group bg-gray-800 rounded-lg overflow-hidden shadow-lg flex flex-col">
                                <div className="w-full aspect-w-1 aspect-h-1 bg-gray-700 overflow-hidden">
                                    <img
                                        src={product.imageUrl}
                                        alt={product.name}
                                        className="w-full h-64 object-cover group-hover:opacity-75 transition-opacity"
                                        onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/600x400/1F2937/FFFFFF?text=Image+Not+Found'; }}
                                    />
                                </div>
                                <div className="p-4 flex flex-col flex-grow">
                                    <h3 className="mt-1 text-lg font-semibold text-white">{product.name}</h3>
                                    <p className="mt-1 text-sm text-gray-400 flex-grow">{product.description}</p>
                                    <p className="mt-2 text-xl font-bold text-indigo-400">${product.price.toFixed(2)}</p>
                                    <button className="mt-4 w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 focus:ring-offset-gray-800">
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </main>
        </div>
    );
};


// --- Main App Component ---
// This is the root component that decides what to show.

export default function App() {
  const [token, setToken] = useState(() => localStorage.getItem('jwt_token'));

  const handleLoginSuccess = (receivedToken) => {
    setToken(receivedToken);
    localStorage.setItem('jwt_token', receivedToken);
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('jwt_token');
  };

  // If we have a token, show the Product Catalog.
  if (token) {
    return <ProductCatalog token={token} handleLogout={handleLogout} />;
  }

  // Otherwise, show the Authentication Form.
  return <AuthForm onLoginSuccess={handleLoginSuccess} />;
}
