import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProductCatalog from './ProductCatalog';
import ShoppingBag from './ShoppingBag';
import OrderConfirmation from './OrderConfirmation';
import CheckoutPage from './CheckoutPage';
import PaymentPage from './PaymentPage';
import AdminPage from './AdminPage';

const HomePage = ({ token, handleLogout }) => {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [cart, setCart] = useState(null);
    const [currentView, setCurrentView] = useState('catalog');
    const [placedOrder, setPlacedOrder] = useState(null);
    const [shippingDetails, setShippingDetails] = useState(null);

    // This useEffect now re-runs when the view changes, ensuring data is fresh.
    useEffect(() => {
        if (!token) return;

        // Fetch products if navigating to catalog or admin page
        if (currentView === 'catalog' || currentView === 'admin') {
            fetchProducts();
        }

        // Fetch cart if navigating to catalog or bag page
        if (currentView === 'catalog' || currentView === 'bag') {
            fetchCart();
        }
        
    }, [token, currentView]); // <-- The key change is adding 'currentView' here

    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:8081/api/products');
            if (!response.ok) throw new Error('Failed to fetch products');
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const fetchCart = async () => {
        try {
            const response = await fetch('http://localhost:8082/api/cart', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) throw new Error('Failed to fetch cart');
            const data = await response.json();
            setCart(data);
        } catch (error) {
            console.error("Error fetching cart:", error);
        }
    };
    
    const handleCartUpdate = (updatedCart) => {
        setCart(updatedCart);
    };

    const handleSelectCategory = (category) => {
        setSelectedCategory(category);
        setCurrentView('catalog');
    };
    
    const handleNavigate = (view) => {
        setCurrentView(view);
    };
    
    const handleAddressSubmit = (address) => {
        setShippingDetails(address);
        setCurrentView('payment');
    };

    const handleOrderPlaced = async (order) => {
        setPlacedOrder(order);
        try {
            await fetch('http://localhost:8082/api/cart', {
                method: 'DELETE',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setCart(null);
        } catch (error) {
            console.error("Error clearing cart:", error);
        }
        setCurrentView('orderConfirmation');
    };

    const renderContent = () => {
        switch(currentView) {
            case 'bag':
                return <ShoppingBag token={token} cart={cart} onCartUpdate={handleCartUpdate} onNavigate={handleNavigate} />;
            case 'checkout':
                return <CheckoutPage onAddressSubmit={handleAddressSubmit} />;
            case 'payment':
                return <PaymentPage token={token} cart={cart} shippingDetails={shippingDetails} onOrderPlaced={handleOrderPlaced} />;
            case 'orderConfirmation':
                return <OrderConfirmation order={placedOrder} onNavigate={handleNavigate} />;
            case 'admin':
                return <AdminPage token={token} products={products} onProductUpdate={fetchProducts} />;
            case 'catalog':
            default:
                const filteredProducts = selectedCategory 
                    ? products.filter(p => p.category === selectedCategory) 
                    : products;
                return <ProductCatalog token={token} products={filteredProducts} onAddToCartSuccess={handleCartUpdate} />;
        }
    };

    const cartItemCount = cart ? cart.items.reduce((sum, item) => sum + item.quantity, 0) : 0;

    return (
        <div className="bg-gray-100 min-h-screen">
            <Header 
                handleLogout={handleLogout} 
                onSelectCategory={handleSelectCategory}
                cartItemCount={cartItemCount}
                onNavigate={handleNavigate}
                userRole={userRole}
            />
            <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {renderContent()}
            </div>
        </div>
    );
};

export default HomePage;
