import React, { useState, useEffect } from 'react';
import AlertMessage from './common/AlertMessage';

const ProductCatalog = ({ token }) => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cartSuccessMessage, setCartSuccessMessage] = useState('');

    const handleAddToCart = async (productId) => {
        setCartSuccessMessage('');
        setError(null);
        try {
            const response = await fetch('http://localhost:8082/api/cart/items', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ productId: productId, quantity: 1 })
            });
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to add item to cart.');
            }
            setCartSuccessMessage('Item added to cart successfully!');
            setTimeout(() => setCartSuccessMessage(''), 3000);
        } catch (err) {
            setError(err.message);
        }
    };

    useEffect(() => {
        const fetchProducts = async () => {
            setIsLoading(true);
            setError(null);
            try {
                const response = await fetch('http://localhost:8081/api/products');
                if (!response.ok) throw new Error(`Failed to fetch products. Status: ${response.status}`);
                const data = await response.json();
                setProducts(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };
        fetchProducts();
    }, []);

    return (
        <main>
            <div className="px-4 py-6 sm:px-0">
                <h1 className="text-3xl font-bold text-white mb-6">Products</h1>
                {isLoading && <div className="text-center text-gray-400">Loading products...</div>}
                {error && <AlertMessage message={`Error: ${error}`} type="error" />}
                {cartSuccessMessage && <AlertMessage message={cartSuccessMessage} type="success" />}
                
                <div className="mt-6 grid grid-cols-1 gap-y-10 sm:grid-cols-2 gap-x-6 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
                    {/* THIS IS THE MISSING PIECE OF CODE */}
                    {products.map((product) => (
                        <div key={product.id} className="group bg-gray-800 rounded-lg overflow-hidden shadow-lg flex flex-col">
                            <div className="w-full aspect-w-1 aspect-h-1 bg-gray-700 overflow-hidden">
                                <img
                                    src={product.imageUrl}
                                    alt={product.name}
                                    className="w-full h-64 object-cover group-hover:opacity-75 transition-opacity"
                                    onError={(e) => { e.target.onerror = null; e.target.src='[https://placehold.co/600x400/1F2937/FFFFFF?text=Image+Not+Found](https://placehold.co/600x400/1F2937/FFFFFF?text=Image+Not+Found)'; }}
                                />
                            </div>
                            <div className="p-4 flex flex-col flex-grow">
                                <h3 className="mt-1 text-lg font-semibold text-white">{product.name}</h3>
                                <p className="mt-1 text-sm text-gray-400 flex-grow">{product.description}</p>
                                <p className="mt-2 text-xl font-bold text-indigo-400">${product.price.toFixed(2)}</p>
                                <button 
                                    onClick={() => handleAddToCart(product.id)}
                                    className="mt-4 w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 focus:ring-offset-gray-800">
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </main>
    );
};

export default ProductCatalog;
