import React from 'react';

const OrderConfirmation = ({ order, onNavigate }) => {
    if (!order) {
        return (
            <div className="text-center p-10 bg-white rounded-lg shadow-md">
                <h1 className="text-2xl font-bold text-red-600">Error</h1>
                <p className="text-gray-600 mt-2">Could not find order details. Please try again.</p>
                <button onClick={() => onNavigate('catalog')} className="mt-6 bg-slate-700 text-white py-2 px-6 rounded-md font-bold hover:bg-slate-800">
                    Back to Shopping
                </button>
            </div>
        );
    }

    return (
        <div className="text-center p-10 bg-white rounded-lg shadow-md max-w-2xl mx-auto">
            <div className="w-16 h-16 bg-green-100 rounded-full mx-auto flex items-center justify-center mb-4">
                <svg className="w-8 h-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="[http://www.w3.org/2000/svg](http://www.w3.org/2000/svg)"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path></svg>
            </div>
            <h1 className="text-3xl font-bold text-gray-800">Order Placed Successfully!</h1>
            <p className="text-gray-600 mt-2">Thank you for your purchase, {order.customerName}.</p>
            <div className="mt-6 border-t border-b border-gray-200 py-4">
                 <p className="text-gray-700">Your order number is:</p>
                 <p className="font-mono text-lg font-semibold text-slate-800 mt-1">{order.orderNumber}</p>
            </div>
            <p className="text-gray-500 text-sm mt-4">A confirmation has been sent to your email and a text to {order.customerPhone}.</p>
            <button onClick={() => onNavigate('catalog')} className="mt-8 bg-slate-700 text-white py-3 px-8 rounded-md font-bold hover:bg-slate-800 transition-colors">
                Continue Shopping
            </button>
        </div>
    );
};

export default OrderConfirmation;
